import unittest
import solution as s

class TestInteraction(unittest.TestCase) :
    def test_example_1(self) :
        self.assertEqual(s.Solution().longestPalindrome("abccccdd"), 7)
    def test_three_same_characters(self) :
        self.assertEqual(s.Solution().longestPalindrome("ccc"), 3)
    def test_super_long(self):
        self.assertEqual(s.Solution().longestPalindrome(""), 983)